\multips(0,0)(0,1.0){7}{\psline[linestyle=dashed,linecap=1,dash=1.5pt 1.5pt,linewidth=0.4pt,linecolor=gray]{c-c}(0,0)(6.64,0)}
\multips(0,0)(1.0,0){8}{\psline[linestyle=dashed,linecap=1,dash=1.5pt 1.5pt,linewidth=0.4pt,linecolor=gray]{c-c}(0,0)(0,5.68)}
\psaxes[labelFontSize=\scriptstyle,xAxis=true,yAxis=true,Dx=1.,Dy=1.,ticksize=-2pt 0,subticks=0]{->}(0,0)(0.,0.)(6.64,5.68)[$t$,140] [\mbox{$c_A(t),c_B(t)$},-40]
\psplot[linewidth=1.2pt,plotpoints=200]{0}{6.640000000000011}{2.0^(-(x-2.0))}
\psplot[linewidth=1.2pt,plotpoints=200]{0}{6.640000000000011}{-2.0^(-(x-2.0))+4.0}
\rput[tl](0.3,3.9){$c_A$}
\rput[tl](3.3,3.48){$c_B$}
\antwort{\psline(1.,2.)(3.,2.)
\psline(3.,2.)(3.,3.5)
\pscustom[fillstyle=solid,opacity=0.8]{
\parametricplot{1.5707963267948966}{3.141592653589793}{0.6*cos(t)+3.|0.6*sin(t)+2.}
