\begin{tabular}{|c|c|c|c|c|}\hline
\cellcolor[gray]{0.9}Linie&\cellcolor[gray]{0.9}Länge&\cellcolor[gray]{0.9}Fahrtdauer&\cellcolor[gray]{0.9}Stationen&\cellcolor[gray]{0.9}Inbetriebnahme\\ \hline
\fcolorbox{red}{red}{\textcolor{white}{U1}}&19\,200\,m&34\,min&24&1978\\ \hline
\fcolorbox{violet}{violet}{\textcolor{white}{U2}}&16\,848\,m&30\,min&20&1980\\ \hline
\fcolorbox{orange}{orange}{\textcolor{white}{U3}}&13\,402\,m&25\,min&21&1991\\ \hline
\fcolorbox{green}{green}{\textcolor{white}{U4}}&16\,361\,m&29\,min&20&1976\\ \hline
\fcolorbox{brown}{brown}{\textcolor{white}{U6}}&17\,347\,m&34\,min&24&1989\\ \hline
\end{tabular}
\end{center}
