\multips(0,0)(1.0,0){7}{\psline[linestyle=dashed,linecap=1,dash=1.5pt 1.5pt,linewidth=0.4pt,linecolor=lightgray]{c-c}(0,0)(0,57.70833333333344)}
\psaxes[labelFontSize=\scriptstyle,xAxis=true,yAxis=true,Dx=1.,Dy=10.,ticksize=-2pt 0,subticks=0]{->}(0,0)(0.,0.)(7,57.70833333333344)[Jahre (t),140] [N(t),-40]

\end{pspicture*}
\end{center}
\antwort{$N(t)=20\cdot 1,2^t; N(1)=24; N(2)=29; N(3)=35 N(5)=50 $}

